mapper.select:
select
from T_PERSON_COMPANY a,T_PERSON b where a.PERSON_ID = b.PERSON_ID and a.IS_ENABLED = '0'
and lower(a.appcode) like lower('%'|| #{appcode,jdbcType=VARCHAR}||'%')
and a.PERSON_ID = #{personId,jdbcType=VARCHAR}
and a.PERSON_CODE = #{personCode,jdbcType=VARCHAR}
and lower(a.PERSON_NAME) like lower('%'|| #{personName,jdbcType=VARCHAR}||'%')

select 

from T_PERSON_COMPANY a,T_PERSON b
where a.PERSON_ID = b.PERSON_ID and IS_ENABLED = '0' and b.PERSON_ID = #{personId,jdbcType=VARCHAR}

SELECT a.PERSON_ID, b.PERSON_CODE, b.PERSON_NAME FROM T_PERSON a INNER JOIN T_PERSON_NORMAL b on a.PERSON_ID = b.PERSON_ID where 1=1
and b.PERSON_CODE = #{personCode,jdbcType=VARCHAR}
and lower(b.PERSON_NAME) like lower('%'|| #{personName,jdbcType=VARCHAR}||'%')
and b.PERSON_TYPE = #{personType,jdbcType=VARCHAR}
AND NOT EXISTS (SELECT PERSON_ID from RE_PERSON_DEPT b where B.PERSON_ID = a.PERSON_ID AND b.TYPE = 0)

SELECT a.PERSON_ID, a.PERSON_CODE, a.PERSON_NAME from T_PERSON_NORMAL a where NOT EXISTS (SELECT PERSON_ID from RE_PERSON_DEPT b where B.PERSON_ID = a.PERSON_ID AND b.TYPE = 0)
and a.PERSON_CODE = #{personCode,jdbcType=VARCHAR}
and lower(a.PERSON_NAME) like lower('%'|| #{personName,jdbcType=VARCHAR}||'%')
and a.PERSON_TYPE = #{personType,jdbcType=VARCHAR}

SELECT b.PERSON_ID personId, b.PERSON_NAME personName, b.PERSON_TYPE personType, b.IS_ENABLED isEnable, c.ROLE_ID roleId FROM RE_PERSON_ROLE a, T_PERSON_NORMAL b, T_ROLE c WHERE a.PERSON_ID = b.PERSON_ID AND a.ROLE_ID = c.ROLE_ID
AND a.ROLE_ID = #{roleId,jdbcType=VARCHAR}
AND b.PERSON_CODE = #{personCode,jdbcType=VARCHAR}
AND b.PERSON_NAME LIKE '%'||#{personName,jdbcType=VARCHAR}||'%'
AND b.PERSON_TYPE = #{personType,jdbcType=VARCHAR}

SELECT 1 FROM T_PERSON p
WHERE
    p.RTX_CODE = #{rtxCode,jdbcType=VARCHAR} 
AND p.PWD = #{pwd,jdbcType=VARCHAR}
AND ROWNUM = 1
